// Get input parameters and validate
const directoryPath = $input.item.json.directory_path;
const loraToken = $input.item.json.lora_token;
const promptTemplate = $input.item.json.prompt_template || 'character';
const enableRefinement = $input.item.json.enable_refinement || false;

if (!directoryPath) {
  throw new Error('directory_path is required. Edit the "üìù Edit These Settings" node.');
}

if (!loraToken) {
  throw new Error('lora_token is required. Edit the "üìù Edit These Settings" node.');
}

// Validate LoRA token
if (!/^[\w-]+$/.test(loraToken)) {
  throw new Error(`Invalid LoRA token format: "${loraToken}". Should contain only letters, numbers, underscores, or dashes`);
}

const config = {
  directory_path: directoryPath,
  lora_token: loraToken,
  prompt_template: promptTemplate,
  detail_level: 'high',
  enable_refinement: enableRefinement,
  quality_threshold: 0.75
};

const prompts = {
  general: "Provide a concise, objective description suitable for training. Describe what is visible: subjects present, key elements, composition/framing, lighting direction/quality/intensity, color palette, materials/textures, and any notable spatial context. Avoid brand names, identities, opinions, or narrative language.",
  object: "Describe this image for object LoRA training. Focus on environment and optics: lighting direction/quality/intensity and shadows/reflections, camera‚Äìsubject relationship (angle, distance, depth of field, framing), background context and support surfaces, and general color/material cues visible without naming or identifying the object specifically. Avoid brand names, unique labels, or subjective adjectives.",
  character: "Describe this image for character LoRA training. Emphasize pose and positioning in space, lighting direction/quality/intensity and shadows, camera‚Äìsubject relationship (angle, distance, focal-length feel, depth of field, framing), and surroundings. Do not list facial identity details or unique identifiers; avoid brand/style words.",
  style: "Describe the overall scene succinctly, then the dominant color palette and the quality of light (direction, softness/hardness, contrast, time-of-day feel). Mention composition tendencies or rendering approach if evident (e.g., minimal, high-contrast, soft-gradation). End the caption with: 'in the style of {{STYLE}}'. Do not identify brands or subjects.",
  scene: "Describe the setting and visible elements with attention to spatial layout and depth cues, composition/framing lines, lighting direction/quality/intensity, and palette. Include materials/textures of major surfaces. Avoid identities, brands, and subjective adjectives."
};

let finalPrompt = prompts[promptTemplate] || prompts.general;
if (loraToken) {
  finalPrompt = `IMPORTANT: Begin your description with the trigger token "${loraToken}". Then provide the rest of the description.\n\n${finalPrompt}`;
}

return {
  config: config,
  prompt: finalPrompt,
  job_id: 'job_' + Date.now(),
  started_at: new Date().toISOString()
};

// Split image filenames into individual items
const stdout = $input.item.json.stdout;
const directoryPath = $input.item.json.config.directory_path;
const config = $input.item.json.config;
const prompt = $input.item.json.prompt;
const jobId = $input.item.json.job_id;

if (!stdout || stdout.trim() === '') {
  throw new Error(`No images found in directory: ${directoryPath}\n\nMake sure the path exists and contains .jpg, .jpeg, .png, or .webp files.`);
}

// Handle both Unix and Windows line endings
const filenames = stdout.trim().split(/\r?\n/);

return filenames.map((filename, idx) => {
  const trimmedFilename = filename.trim();
  // Use forward slashes for consistency (works on both platforms)
  const filepath = `${directoryPath}/${trimmedFilename}`.replace(/\\/g, '/');

  return {
    json: {
      filename: trimmedFilename,
      filepath: filepath,
      image_id: trimmedFilename.replace(/\.[^/.]+$/, ''), // Remove extension
      config: config,
      prompt: prompt,
      job_id: jobId,
      index: idx
    }
  };
});

// Convert binary to base64 data URI for OpenAI
const binaryData = $input.item.binary.data;

if (!binaryData) {
  throw new Error(`No binary data found for file: ${$input.item.json.filepath}`);
}

const mimeType = binaryData.mimeType || 'image/jpeg';
const base64Data = binaryData.data;

// Create data URI that OpenAI Vision accepts
const dataUri = `data:${mimeType};base64,${base64Data}`;

return {
  ...($input.item.json),
  image_data_uri: dataUri
};

