// Process each caption individually
const input = $input.item.json;
const imageId = input.image_id || 'unknown';
const filename = input.filename || 'unknown';

// Handle API failure
if (input.error || !input.choices) {
  return {
    image_id: imageId,
    filename: filename,
    filepath: input.filepath,
    caption: null,
    error: input.error?.message || 'API call failed',
    success: false,
    needs_refinement: false,
    lora_token_valid: false
  };
}

// Extract and score caption
const caption = input.choices[0].message.content.trim();
const words = caption.split(/\s+/);
const wordCount = words.length;

// Get config
const config = input.config || {};
const expectedToken = config.lora_token;

// LoRA token validation
let loraTokenValid = true;
let loraTokenPosition = null;
let loraTokenIssue = null;

if (expectedToken) {
  const firstWord = words[0]?.toLowerCase();
  const tokenMatch = firstWord === expectedToken.toLowerCase();

  if (!tokenMatch) {
    loraTokenValid = false;
    loraTokenIssue = `Expected LoRA token "${expectedToken}" at start, but caption starts with "${words[0] || 'nothing'}"`;
  } else {
    loraTokenPosition = 0;
  }

  const tokenCount = caption.toLowerCase().split(expectedToken.toLowerCase()).length - 1;
  if (tokenCount > 1) {
    loraTokenIssue = `LoRA token "${expectedToken}" appears ${tokenCount} times (should appear once)`;
  }
} else {
  loraTokenValid = true;
}

// Quality checks
const checks = {
  hasMinLength: wordCount >= 10,
  hasMaxLength: wordCount <= 200,
  hasSubject: /\b(person|character|figure|animal|object|subject)\b/i.test(caption),
  hasVisualDetails: /\b(color|light|style|composition|texture|material)\b/i.test(caption),
  noErrors: !/\b(error|unable|cannot|sorry)\b/i.test(caption),
  notTooVague: !/^(This is|The image shows|I see|Here is)/.test(caption),
  hasLighting: /\b(light|shadow|bright|dark|illuminated|backlit|glow)\b/i.test(caption),
  hasComposition: /\b(foreground|background|center|positioned|angle|perspective|frame)\b/i.test(caption),
  loraTokenValid: expectedToken ? loraTokenValid : true
};

const qualityScore = Object.values(checks).filter(Boolean).length / Object.keys(checks).length;
const needsRefinement = config.enable_refinement && qualityScore < (config.quality_threshold || 0.75);

return {
  image_id: imageId,
  filename: filename,
  filepath: input.filepath,
  caption: caption,
  word_count: wordCount,
  quality_score: Math.round(qualityScore * 100) / 100,
  quality_checks: checks,
  needs_refinement: needsRefinement,
  success: true,
  config: config,
  lora_token: expectedToken || null,
  lora_token_valid: loraTokenValid,
  lora_token_position: loraTokenPosition,
  lora_token_issue: loraTokenIssue
};

