// Process refinement
const input = $input.item.json;
const original = $node['Score Quality'].json;

if (input.error || !input.choices) {
  return {
    ...original,
    was_refined: false,
    refinement_failed: true
  };
}

const refined = input.choices[0].message.content.trim();
const words = refined.split(/\s+/);
const wordCount = words.length;

// Re-validate LoRA token
let loraTokenValid = true;
let loraTokenIssue = null;
const expectedToken = original.lora_token;

if (expectedToken) {
  const firstWord = words[0]?.toLowerCase();
  if (firstWord !== expectedToken.toLowerCase()) {
    loraTokenValid = false;
    loraTokenIssue = `Refinement lost LoRA token! Expected "${expectedToken}", got "${words[0]}"`;
  }
}

const checks = {
  hasMinLength: wordCount >= 10,
  hasMaxLength: wordCount <= 200,
  hasSubject: /\b(person|character|animal|object)\b/i.test(refined),
  hasVisualDetails: /\b(color|light|style|composition|texture|material)\b/i.test(refined),
  noErrors: !/\b(error|unable)\b/i.test(refined),
  notTooVague: !/^(This is|The image)/.test(refined),
  hasLighting: /\b(light|shadow|bright|dark)\b/i.test(refined),
  hasComposition: /\b(foreground|background|center|angle)\b/i.test(refined),
  loraTokenValid: expectedToken ? loraTokenValid : true
};

const newScore = Object.values(checks).filter(Boolean).length / Object.keys(checks).length;

return {
  image_id: original.image_id,
  filename: original.filename,
  filepath: original.filepath,
  caption: refined,
  word_count: wordCount,
  quality_score: Math.round(newScore * 100) / 100,
  quality_checks: checks,
  was_refined: true,
  original_quality: original.quality_score,
  improvement: Math.round((newScore - original.quality_score) * 100) / 100,
  success: true,
  lora_token: expectedToken,
  lora_token_valid: loraTokenValid,
  lora_token_issue: loraTokenIssue
};

// Save caption as .txt file next to image
const data = $input.item.json;
const filepath = data.filepath;
const caption = data.caption;

if (!caption) {
  return data; // Skip if no caption
}

// Create .txt filename
const txtPath = filepath.replace(/\.[^/.]+$/, '.txt');

// Convert text to buffer for binary write
const buffer = Buffer.from(caption, 'utf8');

return {
  ...data,
  txt_path: txtPath,
  binary: {
    data: {
      data: buffer.toString('base64'),
      mimeType: 'text/plain',
      fileName: txtPath.split('/').pop()
    }
  }
};

// Aggregate all results
const allItems = $input.all();
const results = allItems.map(item => item.json);

const total = results.length;
const successful = results.filter(r => r.success).length;
const failed = results.filter(r => !r.success).length;
const refined = results.filter(r => r.was_refined).length;

const successfulResults = results.filter(r => r.success && r.caption);
const avgScore = successfulResults.length > 0
  ? successfulResults.reduce((s, r) => s + r.quality_score, 0) / successfulResults.length
  : 0;
const avgWords = successfulResults.length > 0
  ? successfulResults.reduce((s, r) => s + r.word_count, 0) / successfulResults.length
  : 0;

const loraResults = results.filter(r => r.lora_token);
const loraTokensValid = loraResults.filter(r => r.lora_token_valid).length;
const loraTokensInvalid = loraResults.filter(r => !r.lora_token_valid).length;
const loraIssues = results
  .filter(r => r.lora_token_issue)
  .map(r => ({ image_id: r.image_id, issue: r.lora_token_issue }));

const formatted = results.map(r => ({
  image_id: r.image_id,
  filename: r.filename,
  caption: r.caption,
  success: r.success,
  word_count: r.word_count,
  quality_score: r.quality_score,
  was_refined: r.was_refined || false,
  improvement: r.improvement || 0,
  error: r.error,
  lora_token: r.lora_token || null,
  lora_token_valid: r.lora_token_valid || false,
  lora_token_issue: r.lora_token_issue || null
}));

const jobId = results[0]?.job_id || 'unknown';
const startedAt = results[0]?.config?.started_at || new Date().toISOString();
const completedAt = new Date().toISOString();

return {
  job_id: jobId,
  status: 'completed',
  processing_time: {
    started_at: startedAt,
    completed_at: completedAt,
    duration_seconds: Math.round((new Date(completedAt) - new Date(startedAt)) / 1000)
  },
  results: formatted,
  summary: {
    total_images: total,
    successful: successful,
    failed: failed,
    refined_count: refined,
    avg_quality_score: Math.round(avgScore * 100) / 100,
    avg_word_count: Math.round(avgWords),
    lora_tokens_expected: loraResults.length,
    lora_tokens_valid: loraTokensValid,
    lora_tokens_invalid: loraTokensInvalid,
    lora_issues: loraIssues
  },
  metadata: {
    workflow_version: 'v4.0-local-windows',
    model: 'gpt-4o',
    directory: results[0]?.config?.directory_path || 'unknown'
  }
};
